import React, { useState, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

// 🧩 Mocked CustomDropdown
function CustomDropdown({ label, options, selected, onChange }) {
  return (
    <select value={selected} onChange={(e) => onChange(e.target.value)} style={{ padding: 10, fontSize: 16 }}>
      {options.map((opt) => (
        <option key={opt} value={opt}>{opt}</option>
      ))}
    </select>
  );
}

// 🎉 Mocked CongratulationsModal
function CongratulationsModal({ open, onClose }) {
  if (!open) return null;
  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.6)', display: 'flex',
      alignItems: 'center', justifyContent: 'center', zIndex: 1000
    }}>
      <div style={{ background: '#fff', padding: 40, borderRadius: 8 }}>
        <h2>🎉 Congratulations!</h2>
        <p>You’ve marked the task as Done!</p>
        <button onClick={onClose} style={{ marginTop: 20, padding: 10, fontWeight: 600 }}>Close</button>
      </div>
    </div>
  );
}

const statusOptions = ['All Task', 'Ongoing', 'Pending', 'Collaborative Task', 'Done'];

const statusColors = {
  Pending: '#e040fb',
  Ongoing: '#ffb300',
  Done: '#00c853',
};

export default function TaskDetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [task, setTask] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState('Pending');
  const [loading, setLoading] = useState(true);
  const [showCongrats, setShowCongrats] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const logoutRef = useRef();

  React.useEffect(() => {
    function handleClickOutside(event) {
      if (logoutRef.current && !logoutRef.current.contains(event.target)) {
        setShowLogout(false);
      }
    }
    if (showLogout) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogout]);

  const handleLogout = async () => {
    localStorage.removeItem('userEmail');
    try {
      await fetch('http://localhost:5000/api/logout', {
        method: 'POST',
        credentials: 'include'
      });
    } catch {
      alert('Server logout failed, but local logout complete.');
    }
    navigate('/', { replace: true });
  };

  React.useEffect(() => {
    async function fetchTask() {
      try {
        const res = await fetch(`http://localhost:5000/api/tasks/${id}`);
        if (!res.ok) throw new Error('Failed to fetch task');
        const data = await res.json();
        setTask(data);
        setSelectedStatus(data.status);
      } catch (err) {
        console.error(err);
        alert('Error loading task.');
      } finally {
        setLoading(false);
      }
    }
    fetchTask();
  }, [id]);

  const handleSubmit = async () => {
    await fetch(`http://localhost:5000/api/tasks/${id}/status`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: selectedStatus })
    });
    if (selectedStatus === 'Done') {
      setShowCongrats(true);
    } else {
      navigate('/dashboard');
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    await fetch(`http://localhost:5000/api/tasks/${id}`, { method: 'DELETE' });
    navigate('/dashboard');
  };

  const handleCongratsClose = () => {
    setShowCongrats(false);
    navigate('/dashboard');
  };

  const userEmail = localStorage.getItem('userEmail') || '';
  const userName = userEmail.split('@')[0]?.charAt(0).toUpperCase() + userEmail.split('@')[0]?.slice(1) || 'User';

  if (loading || !task) {
    return (
      <div style={{ padding: 40, textAlign: 'center' }}>
        <span>🔄 Loading task...</span>
      </div>
    );
  }

  return (
    <>
      {/* Header Nav */}
      <div style={{
        width: '100%',
        minHeight: 150,
        background: 'radial-gradient(ellipse at left top, #1b2e2e 60%, #1b2e2e 80%)',
        borderBottomLeftRadius: 32,
        borderBottomRightRadius: 32,
        position: 'relative',
        boxSizing: 'border-box',
        zIndex: 1,
        marginBottom: -60
      }}>
        <div style={{
          display: 'flex', justifyContent: 'space-between',
          padding: '48px 48px 0 48px'
        }}>
          <div style={{ display: 'flex', gap: 32 }}>
            <div style={{ fontSize: '1.25rem', fontWeight: 700, color: '#fff' }}>Tasko</div>
            <div style={{ color: '#4be08a', fontWeight: 600, fontSize: 17, cursor: 'pointer' }}>Task List</div>
            <div style={{ color: '#fff', fontWeight: 500, fontSize: 17, cursor: 'pointer' }}>Spin</div>
          </div>
          <div ref={logoutRef} style={{ display: 'flex', alignItems: 'center' }}>
            <span style={{ color: '#fff', fontWeight: 500, fontSize: 18, marginRight: 8 }}>{userName} M.</span>
            <div style={{
              width: 38, height: 38, borderRadius: '50%',
              background: 'linear-gradient(135deg, #4be08a, #1b2e2e)',
              display: 'flex', alignItems: 'center', justifyContent: 'center',
              color: '#fff', fontWeight: 700, fontSize: 20,
              border: '2px solid #fff', cursor: 'pointer'
            }} onClick={() => setShowLogout((v) => !v)}>
              {userName.charAt(0)}
            </div>
            {showLogout && (
              <div style={{
                position: 'absolute', top: 60, right: 0, background: '#fff',
                borderRadius: 8, boxShadow: '0 2px 12px rgba(0,0,0,0.13)',
                minWidth: 120, zIndex: 10
              }}>
                <button onClick={handleLogout} style={{
                  padding: '10px 20px', fontSize: 16,
                  background: 'none', border: 'none',
                  color: '#e74c3c', fontWeight: 600, width: '100%'
                }}>Log out</button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Main Card */}
      <div style={{
        marginTop: 32, maxWidth: 1100, background: '#fff',
        borderRadius: 16, boxShadow: '0 2px 16px rgba(0,0,0,0.07)',
        padding: '32px 40px', marginLeft: 'auto', marginRight: 'auto'
      }}>
        <CongratulationsModal open={showCongrats} onClose={handleCongratsClose} />
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 18 }}>
          <h2 style={{ fontSize: 22 }}>Task Details</h2>
          <div style={{ display: 'flex', gap: 30 }}>
            <button style={{
              background: '#ffe066', color: '#222', fontWeight: 600,
              borderRadius: 8, padding: '10px 29px', fontSize: 16
            }}>Edit Task</button>
            <button style={{
              background: '#4be08a', color: '#fff', fontWeight: 600,
              borderRadius: 8, padding: '10px 28px', fontSize: 16
            }} onClick={() => navigate('/dashboard')}>Back</button>
          </div>
        </div>
        <div style={{ display: 'flex', gap: 32 }}>
          <div style={{
            width: 90, height: 90, borderRadius: '50%', background: '#4be08a',
            display: 'flex', justifyContent: 'center', alignItems: 'center',
           fontSize: 44, fontWeight: 700, color: '#fff'
          }}>
            📝
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ fontWeight: 700, fontSize: '1.45rem', marginBottom: 4 }}>
              {task?.title || 'Untitled Task'}
            </div>
            <div style={{ color: '#666', fontSize: 15, marginBottom: 0, maxWidth: 600 }}>
              {task?.description || 'No description available.'}
            </div>
          </div>
        </div>

        {/* Date and Status */}
        <div style={{ display: 'flex', alignItems: 'center', gap: 32, marginBottom: 18 }}>
          <div>
            <div style={{ fontWeight: 600, color: '#222', fontSize: 15, marginBottom: 2 }}>End Date</div>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              📅
              <span style={{ fontWeight: 500, fontSize: 16 }}>
                {task.date ? new Date(task.date).toLocaleDateString('en-US', {
                  weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
                }) : 'No date specified'}
              </span>
            </div>
          </div>
          <div style={{ borderLeft: '1px solid #eee', height: 32 }} />
          <div style={{
            fontWeight: 700, color: statusColors[task.status] || '#ffb300',
            fontSize: 22, marginLeft: 8, display: 'flex', alignItems: 'center'
          }}>
            <span style={{ fontSize: 18, marginRight: 8 }}>&bull;</span> {task.status}
          </div>
        </div>

        {/* Status Dropdown */}
        <div style={{ margin: '32px 0 0 0', maxWidth: 320 }}>
          <div style={{ fontWeight: 600, marginBottom: 8, fontSize: 16 }}>Change Status</div>
          <CustomDropdown
            label={selectedStatus}
            options={statusOptions}
            selected={selectedStatus}
            onChange={setSelectedStatus}
            multi={false}
          />
        </div>

        {/* Action Buttons */}
        <div style={{ display: 'flex', gap: 16, marginTop: 36, justifyContent: 'flex-end' }}>
          <button onClick={handleDelete} style={{
            background: '#ffe3e3', color: '#e74c3c', fontWeight: 600,
            border: 'none', borderRadius: 8, padding: '12px 32px', fontSize: 16,
            boxShadow: '0 2px 8px #ffe3e333'
          }}>Delete Task</button>
          <button onClick={handleSubmit} style={{
            background: '#4be08a', color: '#fff', fontWeight: 700,
            border: 'none', borderRadius: 8, padding: '12px 32px', fontSize: 16,
            boxShadow: '0 2px 8px #4be08a33'
          }}>Submit</button>
        </div>
      </div>
    </>
  );
}
