import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('m32220@gmail.com');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Login failed');
      // Redirect to dashboard after successful login
      navigate('/dashboard');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-left">
        {/* Replace with SVG/illustration from Figma */}
        <img src="/login-illustration.png" alt="Login Illustration" className="login-illustration" />
      </div>
      <div className="login-right">
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>Login</h2>
          <p className="login-welcome">WelcomeBack, Please Enter your Details to Log In.</p>
          <label>Email Address</label>
          <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
          <label>Password</label>
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
          <div className="login-options">
            <label className="remember-me">
              <input type="checkbox" checked={remember} onChange={e => setRemember(e.target.checked)} />
              Remember me
            </label>
            <Link to="/reset-password" className="forgot-password">Forgot password ?</Link>
          </div>
          {error && <div className="login-error">{error}</div>}
          <button type="submit" className="login-btn">Log In</button>
          <div className="login-or">Or</div>
          <div className="login-signup">Don't have an account? <Link to="/signup">Sign Up</Link></div>
        </form>
      </div>
    </div>
  );
};

export default Login;
