import React, { useEffect, useState } from 'react';

import CongratulationsModal from './CongratulationsModal';
import DeleteConfirmModal from './DeleteConfirmModal';
import DashboardHeader from './DashboardHeader';
import './DashboardHeader.css';
import './Dashboard.css';

const statusColors = {
  Pending: '#e040fb',
  InProgress: '#ffb300',
  Done: '#00c853',
};

const categories = [
  'Arts and Craft', 'Nature', 'Family', 'Sport', 'Friends', 'Meditation'
];

function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [filterCategory, setFilterCategory] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [showCongrats, setShowCongrats] = useState(false);
  const [deleteId, setDeleteId] = useState(null);
  const [showDelete, setShowDelete] = useState(false);

  const fetchTasks = async () => {
    const res = await fetch('http://localhost:5000/api/tasks');
    const data = await res.json();
    setTasks(data);
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleStatusChange = async (id, status) => {
    await fetch(`http://localhost:5000/api/tasks/${id}/status`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status })
    });
    if (status === 'Done') setShowCongrats(true);
    fetchTasks();
  };

  const handleDelete = async (id) => {
    setDeleteId(id);
    setShowDelete(true);
  };

  const confirmDelete = async () => {
    await fetch(`http://localhost:5000/api/tasks/${deleteId}`, { method: 'DELETE' });
    setShowDelete(false);
    setDeleteId(null);
    fetchTasks();
  };

  const filteredTasks = tasks.filter(task =>
    (!filterCategory || task.category === filterCategory) &&
    (!filterStatus || task.status === filterStatus)
  );

  return (
    <>
      <DashboardHeader />
      <div className="dashboard-container">
        <div style={{ margin: '24px 0 18px 0' }}>
          <div style={{ fontSize: '1.25rem', fontWeight: 600, color: '#4be08a' }}>Hi Thomas</div>
          <div className="dashboard-title" style={{ fontSize: '2.1rem', fontWeight: 700, marginBottom: 8 }}>Welcome to Dashboard</div>
        </div>
        <div className="dashboard-header-row">
          <div className="dashboard-title-list">All Task List</div>
          <div className="dashboard-filters">
            <select value={filterCategory} onChange={e => setFilterCategory(e.target.value)}>
              <option value="">Select Task Category</option>
              {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
            </select>
            <select value={filterStatus} onChange={e => setFilterStatus(e.target.value)}>
              <option value="">All Task</option>
              <option value="Pending">Pending</option>
              <option value="InProgress">InProgress</option>
              <option value="Done">Done</option>
            </select>
            <button className="add-task-btn">Add New Task</button>
          </div>
        </div>
        <div className="dashboard-task-cards">
          {filteredTasks.map(task => (
            <div className="dashboard-task-card" key={task._id}>
              <div className="dashboard-task-header">
                <div className="dashboard-task-avatar">B</div>
                <div className="dashboard-task-title">{task.title}</div>
                <button className="dashboard-task-delete" onClick={() => handleDelete(task._id)} title="Delete">üóëÔ∏è</button>
              </div>
              <div className="dashboard-task-desc">{task.description}</div>
              <div className="dashboard-task-meta">
                <span className="dashboard-task-date">{new Date(task.date).toLocaleDateString()}</span>
                <select value={task.status} onChange={e => handleStatusChange(task._id, e.target.value)}>
                  <option value="Pending">Pending</option>
                  <option value="InProgress">InProgress</option>
                  <option value="Done">Done</option>
                </select>
                <span className="dashboard-task-status" style={{ color: statusColors[task.status] }}>{task.status}</span>
              </div>
            </div>
          ))}
        </div>
        <CongratulationsModal open={showCongrats} onClose={() => setShowCongrats(false)} />
        <DeleteConfirmModal open={showDelete} onConfirm={confirmDelete} onCancel={() => setShowDelete(false)} />
      </div>
    </>
  );
}

export default Dashboard;
