import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import CustomDropdown from './CustomDropdown';
import CongratulationsModal from './CongratulationsModal';
import './Dashboard.css';

const statusOptions = [
  'All Task', 'Ongoing', 'Pending', 'Collaborative Task', 'Done'
];

const statusColors = {
  Pending: '#e040fb',
  InProgress: '#ffb300',
  Done: '#00c853',
};


export default function TaskDetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [task, setTask] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState('Pending');
  const [loading, setLoading] = useState(true);
  const [showCongrats, setShowCongrats] = useState(false);

  React.useEffect(() => {
    fetch(`http://localhost:5000/api/tasks/${id}`)
      .then(res => res.json())
      .then(data => {
        setTask(data);
        setSelectedStatus(data.status);
        setLoading(false);
      });
  }, [id]);

  const handleSubmit = async () => {
    await fetch(`http://localhost:5000/api/tasks/${id}/status`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: selectedStatus })
    });
    if (selectedStatus === 'Done') {
      setShowCongrats(true);
    } else {
      navigate('/dashboard');
    }
  };

  const handleDelete = async () => {
    await fetch(`http://localhost:5000/api/tasks/${id}`, { method: 'DELETE' });
    navigate('/dashboard');
  };

  const handleCongratsClose = () => {
    setShowCongrats(false);
    navigate('/dashboard');
  };

  if (loading || !task) return <div style={{ padding: 40 }}>Loading...</div>;

  return (
    <div className="dashboard-container" style={{ marginTop: 40, maxWidth: 900 }}>
      <CongratulationsModal open={showCongrats} onClose={handleCongratsClose} />
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 18 }}>
        <h2>Task Details</h2>
        <div>
          <button style={{ background: '#ffe066', color: '#222', fontWeight: 600, border: 'none', borderRadius: 8, padding: '10px 28px', fontSize: 16, marginRight: 12 }}>Edit Task</button>
          <button style={{ background: '#4be08a', color: '#fff', fontWeight: 600, border: 'none', borderRadius: 8, padding: '10px 28px', fontSize: 16 }} onClick={() => navigate('/dashboard')}>Back</button>
        </div>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 18, marginBottom: 18 }}>
        <div style={{ width: 80, height: 80, borderRadius: '50%', background: '#4be08a', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 38, fontWeight: 700, color: '#fff' }}>
          <span role="img" aria-label="icon">📝</span>
        </div>
        <div>
          <div style={{ fontWeight: 700, fontSize: '1.5rem' }}>{task.title}</div>
          <div style={{ color: '#666', marginTop: 6, maxWidth: 420 }}>{task.description}</div>
        </div>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 32, marginBottom: 18 }}>
        <div>
          <div style={{ fontWeight: 600, color: '#222', fontSize: 15 }}>End Date</div>
          <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginTop: 2 }}>
            <span role="img" aria-label="calendar">📅</span>
            <span style={{ fontWeight: 500 }}>{new Date(task.date).toLocaleDateString()}</span>
          </div>
        </div>
        <div style={{ borderLeft: '1px solid #eee', height: 32 }} />
        <div style={{ fontWeight: 600, color: '#222', fontSize: 15 }}> <span style={{ color: statusColors[task.status], fontWeight: 700, fontSize: 22, marginLeft: 8 }}>&bull; {task.status}</span></div>
      </div>
      <div style={{ margin: '32px 0 0 0' }}>
        <div style={{ fontWeight: 600, marginBottom: 8 }}>Change Status</div>
        <CustomDropdown
          label={selectedStatus}
          options={statusOptions}
          selected={selectedStatus}
          onChange={setSelectedStatus}
          multi={false}
        />
      </div>
      <div style={{ display: 'flex', gap: 16, marginTop: 36, justifyContent: 'flex-end' }}>
        <button onClick={handleDelete} style={{ background: '#ffe3e3', color: '#e74c3c', fontWeight: 600, border: 'none', borderRadius: 8, padding: '12px 32px', fontSize: 16 }}>Delete Task</button>
        <button onClick={handleSubmit} style={{ background: '#4be08a', color: '#fff', fontWeight: 700, border: 'none', borderRadius: 8, padding: '12px 32px', fontSize: 16 }}>Submit</button>
      </div>
    </div>
  );
}
